mapper.select:
select
from tb_newbee_mall_seckill s where is_deleted = 0
and s.goods_id = #{goodsId}
and s.seckill_status = #{seckillStatus}
and DATE_FORMAT(s.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(s.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')

select count(*) from tb_newbee_mall_seckill s where is_deleted = 0
and s.goods_id = #{goodsId}
and s.seckill_status = #{seckillStatus}
and DATE_FORMAT(s.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(s.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')

mapper.insert:
update tb_newbee_mall_seckill
set seckill_num = seckill_num + 1
where seckill_id = #{seckillId}

mapper.select:
call execute_seckill(
        #{seckillId, jdbcType = BIGINT, mode = IN },
        #{userId, jdbcType = BIGINT, mode = IN },
        #{killTime, jdbcType = TIMESTAMP, mode = IN },
        #{result, jdbcType = INTEGER, mode = OUT }
    )
